import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Modal
} from "react-native";
import {React, useState} from 'react'
import CollectionItem from '../components/CollectionItem.js'
import AddCollection from '../components/AddCollection.js'

export default function Dashboard({ navigation }) {
  const [showmodal, changeshowmodal] = useState(false)
  return (
    <View style={styles.container}>
      <Text style={styles.header}>Welcome</Text>
      <Text style={styles.label}>My Collections</Text>
      {/* <View style={styles.hr}></View> */}
      <View style={styles.collections}>
          <CollectionItem num="1" items="3"/>
          <CollectionItem num="2" items="4"/>
          <CollectionItem num="3" items="8"/>
      </View>
      <TouchableOpacity style={styles.addcoll} onPress={()=> changeshowmodal(true)}>
        <Text style={styles.addcolltext}>Add Collection</Text>
      </TouchableOpacity>
      <AddCollection show={showmodal} changeshowmodal={changeshowmodal}/>
      <StatusBar style="auto" />
    </View>
  );
}


const styles = StyleSheet.create({
  container: {
    flex: 1,
    // backgroundColor: "#11b8f5",
    justifyContent: "center",
    padding: 20,
    display: "flex",
  },
  header: {
    fontSize: 40,
    fontFamily: "sans-serif-condensed",
    // flex:2,
  },
  label: {
    // marginTop:5,
    fontSize: 18,
    flex: 0.5,
    fontWeight:"300",
  },
  screens: {
    flex: 10,
  },
  screen_item: {
    height: 50,
    backgroundColor: "rgba(0,0,0,.5)",
    padding: 2,
    margin: 10,
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: 10,
    // borderBottomColor:"rgba(0,0,0,.5)",
  },
  sub_head: {
    fontSize: 25,
    fontWeight: "500",
    borderRadius: 40,
    // color:"#fff",
  },
  hr: {
    width: "100%",
    height: 1,
    backgroundColor: "#000",
  },
  collections: {
    flex: 9,
    display: "flex",
    // backgroundColor: "#fff",
    // margin:5,
    flexDirection:"column",
  },
  item:{
    // flex:1,
    borderWidth:1,
    height:50,
    marginTop:5,
    marginBottom:5,
    padding:5,
    display:"flex",
    justifyContent:"space-between",
    borderRadius:10,
  },
  addcoll:{
    borderWidth:1,
    height:50,
    margin:5,
    padding:5,
    display:"flex",
    justifyContent:"center",
    alignItems:"center",
    borderRadius:10,
    backgroundColor:"black",
  },
  addcolltext:{
    color:"white",
    fontSize:20,
  },
  modalcontainer:{
    height:50,
    width:50,
    backgroundColor:"black"
  },
  modalform:{},
});
