import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Modal,
} from "react-native";

export default function AddCollection({ navigation, show, changeshowmodal }) {
  return (
    <Modal visible={show} style={styles.modalcontainer} animationType="slide">
      <View style={styles.modalform}>
        <Text style={styles.header}>Add Collection</Text>
        <TextInput style={styles.text_box} placeholder="Collection name" />
        <TextInput
          style={styles.text_box}
          placeholder="Collection description"
        />
        <View style={styles.btncontainer}>
          <TouchableOpacity
            style={styles.Sign_in_btn}
            onPress={() => navigation.navigate("Dashboard")}
          >
            <Text style={styles.btn_label}>Add</Text>
          </TouchableOpacity>
          <TouchableOpacity
            style={styles.Sign_in_btn}
            onPress={() => changeshowmodal(false)}
          >
            <Text style={styles.btn_label}>Cancel</Text>
          </TouchableOpacity>
        </View>
      </View>
    </Modal>
  );
}

const styles = StyleSheet.create({
  modalcontainer: {},
  modalform: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    marginTop: 250,
  },
  header: {
      fontSize:20,
  },
  text_box: {
    backgroundColor: "#f5f5f5",
    color: "#000",
    borderRadius: 10,
    height: 45,
    padding: 10,
    margin: 10,
    fontSize: 16,
    width:300,
},
btncontainer:{
    display:"flex",
    flexDirection:"row",
    justifyContent:"space-between",
    width:300,
  },
  Sign_in_btn: {
    backgroundColor:"#a5a5a5",
    height:50,
    width:130,
    margin:3,
    marginLeft:"auto",
    marginRight:"auto",
    borderRadius:15,
    display:"flex",
    justifyContent:"center",
    alignItems:"center",
  },
  btn_label:{
    fontWeight:"bold",
    fontSize:15,
    color:"#fff",
  },
});
