import { StatusBar } from "expo-status-bar";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  Image,
} from "react-native";

const logo = require("../assets/img/logo.png");
export default function Login({ navigation }) {
  return (
    <View style={styles.container}>
      <View style={styles.top_section}>
        <Image source={logo} style={styles.logo} />
      </View>
      <View style={styles.bottom_section}>
        <Text style={styles.header}>LOGIN</Text>
        <Text style={styles.msg}>
          Please enter your login detail down below.
        </Text>
        <TextInput style={styles.text_box} placeholder="Username" />
        <TextInput style={styles.text_box} placeholder="Passowrd" />
        <TouchableOpacity style={styles.Sign_in_btn} onPress={()=>navigation.navigate('Dashboard')}>
          <Text style={styles.btn_label}>LOGIN</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.Sign_in_btn} onPress={()=>navigation.navigate('CreateAccount')}>
          <Text style={styles.btn_label}>CREATE ACCOUNT</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.forgot_btn} >
          <Text style={styles.forgot_btn_label}>Forgot Passowrd</Text>
        </TouchableOpacity>
      </View>
      <StatusBar style="auto" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#11b8f5",
    justifyContent: "center",
    padding: 20,
    display: "flex",
  },
  top_section: {
    // backgroundColor: "",
  },
  bottom_section: {
    backgroundColor: "#fff",
    marginTop: 20,
    padding: 10,
    borderRadius:10,
  },
  logo: {
    marginLeft: "auto",
    marginRight: "auto",
    borderRadius: 50,
  },
  header: {
    textAlign: "center",
    fontSize: 25,
    fontWeight: "bold",
  },
  msg: {
    textAlign: "center",
    fontSize: 17,
  },
  text_box: {
    backgroundColor: "#a7a7a7",
    color: "#000",
    borderRadius: 10,
    height: 45,
    padding: 10,
    margin: 10,
    fontSize: 16,
  },
  Sign_in_btn: {
    backgroundColor:"#f00",
    height:50,
    width:180,
    margin:3,
    marginLeft:"auto",
    marginRight:"auto",
    borderRadius:15,
    display:"flex",
    justifyContent:"center",
    alignItems:"center",
  },
  btn_label:{
    fontWeight:"bold",
    fontSize:15,
    color:"#fff",
  },
  forgot_btn_label:{
    fontWeight:"100",
    fontSize:15,
    color:"#59d3ff",
    textAlign:"center"
  },
});
